val base64String =
    ""

@Composable
fun Greeting(name: String, modifier: Modifier = Modifier) {
    var continuousMediaPlayer = remember { ContinuousMediaPlayer() }
    val coroutineScope = rememberCoroutineScope()
    Column(modifier = Modifier.fillMaxSize()) {
        Text(
            text = "Hello $name!",
            modifier = modifier
        )
        Column(
            modifier = Modifier.fillMaxSize(),
            verticalArrangement = Arrangement.Bottom
        ) {
            Row() {
                Button(
                    onClick = {
                        coroutineScope.launch {
                            continuousMediaPlayer.play()
                        }
                    },
                    enabled = true
                ) {
                    Text("play")
                }

                Button(
                    onClick = {
                        continuousMediaPlayer.stop()
                    }
                ){
                    Text(text = "stop!")
                }
            }
            Row(){
                Button(
                    onClick = {
                        coroutineScope.launch {
                            continuousMediaPlayer.addToQueue(base64String)
                        }
                    }
                ) {
                    Text(text = "add to queue")
                }
                Button(
                    onClick = {
                        coroutineScope.launch {
                            continuousMediaPlayer.addToQueue(null)
                        }
                    }
                ) {
                    Text(text = "add stop to queue")
                }
                Button(
                    onClick = {
                        continuousMediaPlayer.clearQueue()
                    }
                ){
                    Text(text = "Clear queue!")
                }
            }
        }
    }
}

